00033 int obtenirValeurLettre(const struct SacDeJetons *sac, char lettre) {
00034     for (int i = 0; i < NOMBRE_TOTAL_DE_JETONS; ++i) {
00035         if (sac->jetons[i].lettre == lettre) {
00036             return sac->jetons[i].valeur;
