00038     }
00039     // Gestion d'erreur : lettre non trouvée dans le sac
00040     fprintf(stderr, "Erreur : Lettre '%c' non trouvée dans le sac de jetons.\n", lettre);
00041     exit(EXIT_FAILURE);
