00044 int calculerScoreMot(struct Plateau *plateau, struct Joueur *joueur, struct SacDeJetons *sac) {
00045     int scoreMot = 0;
00046     int multiplicateurMot = 1;
00047 
00048     // Calculer le score du mot en tenant compte des cases bonus sur le plateau
00049     for (int i = 0; i < strlen(joueur->dernierMot); ++i) {
00050         int valeurLettre = obtenirValeurLettre(joueur->dernierMot[i], sac);
00051 
00052         // Obtenir les coordonnées de la lettre sur le plateau
00053         int ligne = joueur->dernierLigne;
00054         int colonne = joueur->dernierColonne + i;
00055 
00056         // Vérifier les cases bonus (à ajuster selon les règles du Scrabble)
00057         switch (plateau->bonus[ligne][colonne]) {
00058             case '§':  // Mot triple
00059                 multiplicateurMot *= 3;
00060                 scoreMot += valeurLettre;
00061                 break;
00062             case '@':  // Mot double
00063                 scoreMot += valeurLettre;
00064                 break;
00065             case '&':  // Lettre double
00066                 scoreMot += valeurLettre * 2;
00067                 break;
00068             case '%':  // Lettre triple
00069                 scoreMot += valeurLettre * 3;
00070                 break;
00071             default:
00072                 scoreMot += valeurLettre;
00073                 break;
00074         }
00075     }
00076 
00077     // Appliquer le multiplicateur de mot
00078     scoreMot *= multiplicateurMot;
00079 
00080     return scoreMot;
00081 }
