00083 // Implémentation de la fonction motValide dans scrabble_functions.c
00084 bool motValide(const char *mot) {
00085     FILE *dictionnaire = fopen(DICTIONARY_FILE_PATH, "r");
00086     if (dictionnaire == NULL) {
00087         perror("Erreur lors de l'ouverture du dictionnaire");
00088         exit(EXIT_FAILURE);
00089     }
00090 
00091     char motDictionnaire[50];
00092     while (fgets(motDictionnaire, sizeof(motDictionnaire), dictionnaire) != NULL) {
00093         // Retirez le caractère de nouvelle ligne de la fin du mot
00094         strtok(motDictionnaire, "\n");
00095 
00096         // Comparez le mot avec celui du dictionnaire (ignorant la casse)
00097         if (strcasecmp(mot, motDictionnaire) == 0) {
00098             fclose(dictionnaire);
00099             return true;  // Le mot est dans le dictionnaire
00100         }
00101     }
00102 
00103     fclose(dictionnaire);
00104     return false;  // Le mot n'est pas dans le dictionnaire
00105 }
