00496 // Fonction pour piocher des lettres depuis le sac
00497 void piocherLettres(struct SacDeJetons *sac, struct Joueur *joueur, int nbLettres) {
00498     // Vérifier si le sac contient suffisamment de lettres
00499     if (sac->nbJetonsRestants < nbLettres) {
00500         printf("Le sac ne contient pas suffisamment de lettres.\n");
00501         return;
00502     }
00503 
00504     // Piocher le nombre spécifié de lettres
00505     for (int i = 0; i < nbLettres; ++i) {
00506         // Vérifier si le sac n'est pas vide
00507         if (sac->nbJetonsRestants > 0) {
00508             // Ajouter une lettre au chevalet du joueur depuis le sac
00509             joueur->chevalet[i] = sac->jetons[sac->nbJetonsRestants - 1].lettre;
00510             // Décrémenter le nombre de jetons restants dans le sac
00511             --sac->nbJetonsRestants;
00512         } else {
00513             printf("Le sac est vide. Aucune lettre disponible pour piocher.\n");
00514             return;
00515         }
00516     }
00517 
00518     // Indiquer la fin du chevalet avec un caractère nul
00519     joueur->chevalet[nbLettres] = '\0';
00520 
00521     // Informer le joueur des lettres piochées
00522     printf("%s a pioché les lettres : %s\n", joueur->nom, joueur->chevalet);
00523 }
